$(function(){
    $('.wrapper__box').tilt({
        glare: true,
        // Настройка бликов на объекте
        maxGlare: 0,
        //настрой перспективы
        perspective: 800,
        // Увеличение при наведении
        scale:         1.1,
        // Скорость
        speed:          500,
    })
    
    $('.wrapper__btn').click(function () {
        let Y;
        let A = Number($('#input__A').val());
        let B = Number($('#input__B').val());
        let C = Number($('#input__C').val());
        let X = Number($('#input__X').val());
        
        if(A !== "" && B !== "" && C !== "" && X !== "") 
        {
            if ((A < 0) && (C != 0))
            {
                $('.wrapper__function').text('y = a * x^2 + b * x + c');
                Y = A * X * X + (B * X) + C;
            }
            else if((A > 0) && (C == 0)) 
            {
                if(X != C) 
                {
                    $('.wrapper__function').text('y = -a / (x - c)');
                    Y = -A / (X - C)
                }
                else 
                {
                    alert("Данной функции не существует")
                }
            }
            else 
            {
                $('.wrapper__function').text('y = a * (x + c)');
                Y = A * (X + C)
            }
            $('.wrapper__result').text('y = ' + Y);
        }
        else 
        {
            alert("Дай значение всем переменным")
        }
    });
});
